import React, { useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const Delete = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const URI = process.env.REACT_APP_API_URL;
   const token = localStorage.getItem("user");

  useEffect(() => {
    const token = localStorage.getItem("user");
    if (!token) {
      alert("Unauthorized. Please login first.");
      navigate("/login");
      return;
    }
  },[])
    const HandleDelete=(id)=>{

    
    axios.delete(`${URI}/deleteEmployee`, {
      headers: {
        Authorization: `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      data: { id: id }  // ðŸ‘ˆ sending emp_id in body
    })
    .then(res => {
      console.log("Employee deleted", res.data);
      alert("Employee deleted successfully");
      navigate('/profile/Employee');
    })
    .catch(err => {
      console.error("Error deleting employee", err);
      alert("Failed to delete employee");
      navigate("/employeeList");
    });

  }

  return <div class="" tabindex="-1">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title bg-danger">Are you sure you want to discard this employee record?</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <p></p>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">cancel</button>
        <button type="button" class="btn btn-danger" onClick={()=>HandleDelete(id)}>discard</button>
      </div>
    </div>
  </div>
</div>
  };

export default Delete;
