import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';

const EmployeeList = () => {
    const URI="http://localhost:4000"
    const[empList,setEmpList]=useState([])
    useEffect(()=>{
        axios.get(URI+"/employees")
        .then(res=>{
            setEmpList(res.data)
        })
    },[])
  return (
    <div className='d-flex'>
        <ol>
            {empList.map((emp,ind)=>
            //<li className='border border-2' key={ind}>{emp.uname} <div> <button className="btn btn-warning"> edit</button><button className="btn btn-danger"> delete</button></div></li>
           <li>
           <div class="d-flex bd-highlight">
            <div class="p-2 flex-grow-1 bd-highlight">{emp.uname}</div>
            <div class="p-2 bd-highlight"><button className="btn btn-warning">edit</button></div>
            <div class="p-2 bd-highlight"><button className="btn btn-danger">delete</button></div>
          </div>
           </li>)}
        </ol>
    </div>
  )
}

export default EmployeeList
