import axios from 'axios'
import React, { useEffect } from 'react'
import {useNavigate} from "react-router-dom"


const Profile = () => {
  const URI="http://localhost:4000"
  let userData={}
  const navigate=useNavigate()
  useEffect(()=>{
    token=localStorage.getItem('user')
    if (token===null){
      navigate("/")
    }else{
      axios.post(URI+"/userData",token)
      .then(res=>{
        userData=res.data;
      })
    }
  },[])
  
  
  const clickHandler=()=>{

    navigate("/")
  }
   
  return (
    <div>
      <h1>uname:</h1>{userData.uname}
      <h1>uname:</h1>{userData.jobTitle}
      <h1>uname:</h1>{userData.hireDate}
      <h1>uname:</h1>{userData.department}
      <h1>uname:</h1>{userData.contact}
      

      <button className="btn btn-danger" onClick={clickHandler}> Logout</button>
    </div>
  )
}

export default Profile
