import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate, Link, Outlet, useLocation } from "react-router-dom";

const Profile = () => {
  const URI = process.env.REACT_APP_API_URL;
  const location = useLocation();
  const [userData, setUserData] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('user');
    if (!token) {
      navigate("/");
      return;
    }

    axios.post(`${URI}/userData`, {}, { headers: { Authorization: `Bearer ${token}` } })
      .then(res => setUserData(res.data))
      .catch(err => console.log("error occurred"));
  }, [location.pathname]);

  const handleLogout = () => {
    localStorage.clear();
    navigate("/");
  };

  return (
    <div className="container mt-4 position-relative">
      {/* Logout button at top-right */}
      <button 
        className="btn btn-danger position-absolute top-0 end-0"
        style={{ margin: "10px" }}
        onClick={handleLogout}
      >
        Logout
      </button>

      <h2 className="mb-4">Profile</h2>

      <div className="card p-4 mb-3">
        {["uname", "jobTitle", "hireDate", "department", "contact"].map((field) => (
          <div className="d-flex justify-content-between mb-2" key={field}>
            <strong>{field.charAt(0).toUpperCase() + field.slice(1)}:</strong>
            <span>{userData[field]}</span>
          </div>
        ))}
      </div>

      <div>
        <Link to="/profile/employeeList" className="btn btn-info">
          Employees List
        </Link>
      </div>

      <Outlet />
    </div>
  );
};

export default Profile;
