import axios from 'axios'
import React, { useEffect, useState } from 'react'
import {resolvePath, useNavigate} from "react-router-dom"


const Profile = () => {
  const URI="http://localhost:4000"
  const[userData,setUserData]=useState({})
  const navigate=useNavigate()
  useEffect(()=>{
    const token=localStorage.getItem('user')
    
    if (token==="undefined"){
      navigate("/")
    }else{
      axios({
        method: 'post',
        url:URI+"/userData" ,
        headers:{
          Authorization:"Bearer "+token
        }
      }).then(res=>{
        console.log(res)
       setUserData(res.data)
       console.log(userData)
      }).catch(err=>console.log("error occured"))
      // axios.post(URI+"/userData",{token})
      // .then(res=>{
      //   userData=res.data;
      // })
      // .catch(err=>console.log("user not found"))
    }
  },[])
  
  
  const clickHandler=()=>{
    localStorage.removeItem("user")
    navigate("/")
  }
   
  return (
    <div>
      <span><b>uname:</b></span><span><p>{userData.uname}</p></span>
      <span><b>jobTitle:</b></span><span><p>{userData.jobTitle}</p></span>
      <span><b>hiredate:</b></span><span><p>{userData.hireDate}</p></span>
      <span><b>hiredate:</b></span><span><p>{userData.hireDate}</p></span>
      <span><b>department:</b></span><span><p>{userData.department}</p></span>
      <span><b>contact:</b></span><span><p>{userData.contact}</p></span>
      

      <button className="btn btn-danger" onClick={clickHandler}> Logout</button>
    </div>
  )
}

export default Profile
