import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const Delete = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const URI = process.env.REACT_APP_API_URL;
  const token = localStorage.getItem("user");

  const [employee, setEmployee] = useState(null);

  // ---------------------------
  // FETCH EMPLOYEE DETAILS HERE
  // ---------------------------
  useEffect(() => {
    if (!token) {
      alert("Unauthorized. Please login first.");
      navigate("/login");
      return;
    }

    axios.post(`${URI}/Employee`, { id })
      .then(res => {
        setEmployee(res.data);
        console.log(res.data)
      })
      .catch(err => {
        console.error("Error fetching employee", err);
      });

  }, [id, navigate, token, URI]);


  // ---------------------------
  // DELETE EMPLOYEE FUNCTION
  // ---------------------------
  const HandleDelete = (id) => {

    axios.delete(`${URI}/deleteEmployee`, {
      headers: {
        Authorization: `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      data: { id }
    })
      .then(res => {
        alert("Employee deleted successfully");
        navigate('/profile/Employee');
      })
      .catch(err => {
        console.error("Error deleting employee", err);
        alert("Failed to delete employee");
        navigate("/employeeList");
      });
  };

  return (
    <div className="container mt-5">

      <h3 className="text-danger mb-3">Delete Employee</h3>

      <div className="card p-3">

        <p>
          {employee ? (
            <>
              Are you sure you want to delete <strong>{employee.name}</strong>?
            </>
          ) : (
            "Loading employee details..."
          )}
        </p>

        <div className="modal-footer">
          <button type="button" className="btn btn-secondary" onClick={() => navigate(-1)}>Cancel</button>

          <button type="button" className="btn btn-danger" onClick={() => HandleDelete(id)}>
            Delete
          </button>
        </div>

      </div>
    </div>
  );
};

export default Delete;
