import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate, Link, Outlet, useLocation } from "react-router-dom";

const Profile = () => {
  const URI = process.env.REACT_APP_API_URL;
  const location = useLocation();
  const [userData, setUserData] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('user');
    if (!token) {
      navigate("/");
      return;
    }

    axios.post(`${URI}/userData`, {}, { headers: { Authorization: `Bearer ${token}` } })
      .then(res => setUserData(res.data))
      .catch(err => console.log("Error fetching user data", err));
  }, [location.pathname]);

  const handleLogout = () => {
    localStorage.clear();
    navigate("/");
  };

  return (
    <div className="container d-flex flex-column justify-content-center align-items-center mt-5" style={{ minHeight: '80vh' }}>
      
      {/* Logout Button */}
      <button className="btn btn-danger mb-4" onClick={handleLogout}>
        Logout
      </button>

      {/* Profile Card */}
      <div className="card shadow p-4 w-100" style={{ maxWidth: "500px" }}>
        <h3 className="text-center mb-4">Profile</h3>

        <div className="mb-2"><strong>Name:</strong> {userData.uname}</div>
        <div className="mb-2"><strong>Job Title:</strong> {userData.jobTitle}</div>
        <div className="mb-2"><strong>Hire Date:</strong> {userData.hireDate}</div>
        <div className="mb-2"><strong>Department:</strong> {userData.department}</div>
        <div className="mb-2"><strong>Contact:</strong> {userData.contact}</div>

        <div className="d-flex justify-content-center mt-3">
          <Link to="/profile/employeeList" className="btn btn-info me-2">Employees List</Link>
          <Link to={`/profile/edit/${userData._id}`} className="btn btn-primary">Edit Profile</Link>
        </div>
      </div>

      <Outlet />
    </div>
  );
};

export default Profile;
