import axios from 'axios'
import React, { useEffect, useState } from 'react'
import {useNavigate} from "react-router-dom"


const Profile = () => {
  const URI="http://localhost:4000"
  const[userData,setUserData]=useState({})
  const navigate=useNavigate()
  useEffect(()=>{
    const token=localStorage.getItem('user')
    
    if (token===null){
      navigate("/")
    }else{
      axios({
        method: 'post',
        url:URI+"/userData" ,
        headers:{
          Authorization:"Bearer "+token
        }
      }).then(res=>{
       setUserData(res.data)
      }).catch(err=>console.log("error occured"))
      // axios.post(URI+"/userData",{token})
      // .then(res=>{
      //   userData=res.data;
      // })
      // .catch(err=>console.log("user not found"))
    }
  },[])
  
  
  const clickHandler=()=>{
    localStorage.removeItem("user")
    navigate("/")
  }
   
  return (
    <div>
      <h1>uname:</h1>{userData.uname}
      <h1>uname:</h1>{userData.jobTitle}
      <h1>uname:</h1>{userData.hireDate}
      <h1>uname:</h1>{userData.department}
      <h1>uname:</h1>{userData.contact}
      

      <button className="btn btn-danger" onClick={clickHandler}> Logout</button>
    </div>
  )
}

export default Profile
