import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useNavigate, Link, Outlet, useLocation } from "react-router-dom"

const Profile = () => {
  const URI= process.env.REACT_APP_API_URL;
  const location = useLocation();
  const [userData, setUserData] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('user');
    if (!token) {
      navigate("/");
      return;
    }

    axios.post(`${URI}/userData`, {}, { headers: { Authorization: `Bearer ${token}` } })
      .then(res => setUserData(res.data))
      .catch(err => console.log("error occurred"));
  }, [location.pathname]);

  const clickHandler = () => {
    localStorage.clear();
    navigate("/");
  }

  return (
    <div>
      <div className="position-relative top-0 start-50">
        {["uname","jobTitle","hireDate","department","contact"].map((field) => (
          <div className="d-flex mt-2" key={field}>
            <span><b>{field}:</b></span>
            <span><p>{userData[field]}</p></span>
          </div>
        ))}
      </div>     
      <div className="position-relative top-50 start-50">
        <button className="btn btn-danger" onClick={clickHandler}>Logout</button>
      </div>
      <div>
        <button className="btn btn-info mt-3">
          <Link to="/profile/employeeList">Employees List</Link>
        </button>
      </div>
      <Outlet />
    </div>
  )
}

export default Profile;
